//use react booststrap for styling NO CSS it will make it a lot easier to style
import React, { useState } from 'react';
import io from 'socket.io-client';
import { Link } from "react-router-dom";
import { Button } from 'react-bootstrap';
import {Container} from 'react-bootstrap';

function Home(){
    const containerStyle = {
        textAlign: 'center',
        padding: '20px',
        backgroundColor: '#f0f0f0',  // Set your desired background color
        minHeight: '100vh',  // Ensure the container takes at least the full height of the viewport
      };
    const buttonStyle = {
        backgroundColor: '#007bff',  // Set your desired button background color
        color: '#ffffff',  // Set your desired button text color
      };
    return (
        <Container style={containerStyle}>
        <div className="App">
            <header className="App-header">
                <p>Play the Lotto!!</p><p>Chance to Win Even</p>
                <Link to="/picknum" className="btn btn-primary">Start Game</Link>
                <Button style={buttonStyle}>Start Game</Button>{' '}
            </header>

        </div>
        </Container>
    );
}
export default Home;
