import React, { useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3000'); // Connect to your server

const InputComponent = () => {
  const [inputs, setInputs] = useState(['', '', '', '']);

  const handleInputChange = (index, value) => {
    const newInputs = [...inputs];
    newInputs[index] = value;
    setInputs(newInputs);
  };

  const handleSubmit = () => {
    const inputString = inputs.join('');
    socket.emit('user input', inputString); // Send the combined input string to the server
    setInputs(['', '', '', '']); // Clear the input fields
  };

  return (
    <div>
      {inputs.map((input, index) => (
        <input
          key={index}
          type="text"
          value={input}
          onChange={(e) => handleInputChange(index, e.target.value)}
          maxLength="2" // Optional: limit each input box to a single character
        />
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default InputComponent;
